import React, { useContext } from 'react'
import {GlobalState} from '../../GlobalState';

function Filters() {
    const state = useContext(GlobalState);
    const [categories] = state.categoriesApi.categories;
    const [category, setCategory] = state.productsApi.category;
    const [sort, setSort] = state.productsApi.sort;
    const [search, setSearch] = state.productsApi.search;

    const handleFilters = e => {
        setCategory(e.target.value);
        setSearch('');
    }

    return (
        <div className="w-full overflow-hidden flex justify-">
            <div className="">
                <span>Filters: </span>

                <select name="category" value={category} onChange={handleFilters}>
                    <option value="">All Products</option>
                    {
                        categories.map(category => (
                            <option value={"category=" + category._id} key={category._id}>
                                {category.name}
                            </option>
                        ))
                    }
                </select>
            </div>

            <input type="text" value={search} placeholder="Search" onChange={e => setSearch(e.target.value.toLowerCase())} />
        
            <div className="">
                <span>Sort by: </span>

                <select value={sort} onChange={e => setSort(e.target.value)}>
                    <option value="">Newest</option>
                    <option value="sort=oldest">Oldest</option>
                    <option value="sort=-sold">Best sellers</option>
                    <option value="sort=-price">Price: High to Low</option>
                    <option value="sort=price">Price: Low to High</option>
                </select>
            </div>
        </div>
    )
}

export default Filters
